package com.library.librarymanagement.entity;

import jakarta.persistence.*;

/**
 * Entity class representing an author in the library system.
 * <p>
 * This class is mapped to a relational database table through JPA and contains
 * information related to a single author.
 *
 * <h2>Attributes</h2>
 * <ul>
 *     <li><strong>id</strong> — unique identifier, auto-generated by the persistence provider</li>
 *     <li><strong>name</strong> — full name of the author</li>
 *     <li><strong>nationality</strong> — country of origin</li>
 * </ul>
 *
 * <p>
 * The {@code Author} entity is persisted via Spring Data JPA through the
 * {@link com.library.librarymanagement.repository.AuthorRepository AuthorRepository}.
 * </p>
 */
@Entity
public class Author {

    /**
     * Unique identifier for the author.
     * <p>
     * The value is generated automatically using the identity strategy.
     */
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    /**
     * Full name of the author.
     */
    private String name;

    /**
     * Country of origin of the author.
     */
    private String nationality;

    /**
     * Default constructor required by JPA.
     */
    public Author() {
    }

    /**
     * Creates a new instance of {@code Author} with the provided attributes.
     *
     * @param name        the author's full name
     * @param nationality the author's nationality
     */
    public Author(String name, String nationality) {
        this.name = name;
        this.nationality = nationality;
    }

    /**
     * Returns the unique identifier of the author.
     *
     * @return the author's ID
     */
    public Long getId() {
        return id;
    }

    /**
     * Returns the full name of the author.
     *
     * @return the author's name
     */
    public String getName() {
        return name;
    }

    /**
     * Updates the author's full name.
     *
     * @param name new name to assign
     */
    public void setName(String name) {
        this.name = name;
    }

    /**
     * Returns the nationality of the author.
     *
     * @return the author's nationality
     */
    public String getNationality() {
        return nationality;
    }

    /**
     * Updates the nationality of the author.
     *
     * @param nationality new nationality to assign
     */
    public void setNationality(String nationality) {
        this.nationality = nationality;
    }
}
